import React from 'react';
import '../styles/App.scss';
import {useSelector, useDispatch} from 'react-redux';

const App = () => {

  const dispatch = useDispatch();
  const state = useSelector((state) => state);
  const xvalue = useSelector((state)=>state.turn.x);
  const yvalue = useSelector((state)=>state.turn.y);
  console.log(state);

  return (
    <div className="App">
    <button onClick={()=>dispatch({type:'INITILIZE'})}>Initialize Game</button>
      <h1>Battleship</h1>
      <h2>Ready player {state.turn.activePlayer}</h2>
      <input value={xvalue} onChange={()=> dispatch({type:'UPDATECOORDINATES',payload:xvalue}) } />
      <input value={yvalue} onChange={()=> dispatch({type:'UPDATECOORDINATES',payload:yvalue}) } />
        <button onClick={()=> dispatch({type:'FIRE'})}>Fire</button>
    </div>
  );
}

export default App;
