import {useDispatch} from 'react-redux';
const dispatch = useDispatch();


const checkForShip = (player,coordinates)=>{
    for(let i=0;i<player.ships.length;i++){
      let ship=player.ships[i];
      let shipPresent=ship.locations.filter((actualCoordinates)=>{

        if((actualCoordinates[0] === coordinates[0]) && (actualCoordinates[1]===coordinates[1])){

          return ship;

        }
        else{

          return false;
        }
      })[0];

      if(shipPresent){return ship;}

    }
    return false;
}

const damageShip = (ship, coordinates,id)=>{
    ship.damage.push(coordinates);
    dispatch({type:'DAMAGE',payload:coordinates});
}

const fire = (player,coordinates)=>{
  const ship = checkForShip(player,coordinates);
  console.log(player);
    if(ship){
      damageShip(ship,coordinates,player.id)

    }

}




export {checkForShip, damageShip, fire};
