const createSpaces = () => {
  const spaces = [];
  for (let y = 0; y < 10; y++) {
    for (let x = 0; x < 10; x++) {
      spaces.push([x, y]);
    }
  }
  return spaces;
};

const initialState = {
  boards: [
    {
      id: 0,
      spaces: createSpaces(),
      locationsFired: [],
      damage: []
    },
    {
      id: 1,
      spaces: createSpaces(),
      locationsFired: [],
      damage: []
    }
  ]
};

const gameReducer = (state = initialState, action) => {
  switch (action.type) {
    case "INITILIZE":
      return initialState;

    case "DAMAGE":
      const newboards = [...state.boards];
      newboards[action.payload.id].damage.push(action.payload.coordinates);
      if (
        state.boards[action.payload.id].damage.map(arr => {
          if (
            arr[0] === action.payload.coordinates[0] &&
            arr[1] === action.payload.coordinates[1]
          ) {
            return true;
          }
        })
      ) {
        return state;
      } else {
        return Object.assign({}, state, {
          newboards
        });
      }

    case "SHOT":
      const oopboards = [...state.boards];
      oopboards[action.payload.id].locationsFired.push(
        action.payload.coordinates
      );

      return Object.assign({}, state, {
        oopboards
      });

    default:
      return state;
  }
};

export default gameReducer;
