import {fire} from './ship_methods.js';



	const checkGameStatus = (player) => {
		const player1={locations:[],
									 damage:[]};
		player.ships.map(ship =>{
			player1.locations.push(...ship.locations);
			player1.damage.push(...ship.damage);
		});

		if(player1.locations.length === player1.damage.length){
			return "Game Over: You Win!";
		}
		else{ return false; }

}

const takeTurn = (opposingPlayer, guessFunction) => {
	let coordinates = guessFunction;
	fire(opposingPlayer, coordinates);
	let gameOver = checkGameStatus(opposingPlayer);

	return gameOver;
}

export  {checkGameStatus,takeTurn};
