const initialState = {
  activePlayer: 1,
  x:'',
  y:''
};

const turnReducer = (state = initialState , action) =>{
  switch(action.type){
    case 'INITILIZE' : return initialState;
    case 'FIRE' : return Object.assign({}, state, {
      activePlayer:!state.activePlayer,
      x:'',
      y:''
    });
    default : return state;
  }

}

export default playerReducer;
