const initialState = 0;

const rootReducer = (state = initialState , action) => {
  switch(action.type){
    case 'INCREMENT' :
    return Object.assign({}, state, state +1);
    case 'DECREMENT' : return(state -1);
    default : return state;
  }
}

export default rootReducer;
