const initialState = {
  boards: [{
    id:1,
    spaces: createSpaces(),
    shiplocations: [],
    openwater: [],
    locationsFired:[]},
    {
    id:2,
    spaces: createSpaces(),
    shiplocations: [],
    openwater: [],
    locationsFired:[]
  }]
};

const createSpaces = () => {
  const spaces = [];
  for(let y=0 ; y<10 ; y++){
    for(let x=0 ; x<10; x++){
      spaces.push([x,y]);
    }
  }
  return spaces;
}




const gameReducer = (state = initialState , action) => {
  switch(action.type){
    default : return state;
  }
}

export default gameReducer;
