const initialState = {
  counter:0
}

const rootReducer = (state = initialState,action) => {
  switch(action.type){
    case 'INCREMENT' : return(newstate.counter = state.counter+1);
    case 'DECREMENT' : return(state.counter-1);
    default : return state;
  }
}

export default rootReducer;
