import React from 'react';
import '../styles/App.scss';
import {useSelector, useDispatch} from 'react-redux';
import {takeTurn} from '../game_logic/game_instance';

const App = () => {

  const dispatch = useDispatch();
  const state = useSelector((state) => state);
  const xvalue = useSelector((state)=>state.turn.x);
  const yvalue = useSelector((state)=>state.turn.y);
  const gameMessage = useSelector((state)=>state.turn.gameMessage);
  console.log(state);

  const handleXChange = (event) => {
    dispatch({type:'UPDATECOORDINATEX',payload:event.target.value})
    }
  const handleYChange = (event) => {
    dispatch({type:'UPDATECOORDINATEY',payload:event.target.value})
    }

    const handleClick = () => {
      const val = takeTurn(state.players[state.turn.activePlayer],[parseInt(xvalue),parseInt(yvalue)],state.game.boards[state.turn.activePlayer].damage);
      dispatch({type:'FIRE', payload:(!val.gameMessage)?'':val.gameMessage});
      dispatch({type:'DAMAGE',payload:{id:state.turn.activePlayer, coordinates:val.ship}});
    }

  return (
    <div className="App">
    <button onClick={()=>dispatch({type:'INITILIZE'})}>Initialize Game</button>
      <h1>Battleship</h1>
      <h2>Player {state.turn.activePlayer+1}, pick your coordinates!</h2>
      <input value={xvalue} onChange={handleXChange} />
      <input value={yvalue} onChange={handleYChange} />
        <button onClick={handleClick}>Fire</button>
        <h2>{gameMessage}</h2>
    </div>
  );
}

export default App;
